mapper.select:
SELECT  FROM CONSUMPTION_DAILY
WHERE 1=1
AND expense >0 AND expense <=10
AND expense >10 AND expense <=20
AND expense >20 AND expense <=30 
AND expense >30
AND  consump_date >= #{start_time}
AND consump_date <= #{end_time}
AND user_id=#{user_id}
AND is_over=#{is_over}
ORDER BY consump_date DESC LIMIT #{startIndex},10

